import { useState } from "react";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import { v4 as uuidv4 } from 'uuid';

function MyForm() {
  const [todo, setTodo] = useState({ id: uuidv4(), title: "", desc: "" });
  const {id, title, desc} = todo;

  const handleChange = (e) => {
        setTodo({...todo, [e.target.name] : e.target.value});
  }  

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(todo);
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Row className="mb-3">
        <Form.Group as={Col} controlId="formGridEmail">
          <Form.Label style={{ color: "white" }}>Title</Form.Label>
          <Form.Control type="text" placeholder="Enter Ttile" value={title} onChange={handleChange}/>
        </Form.Group>

        <Form.Group as={Col} controlId="formGridPassword">
          <Form.Label style={{ color: "white" }}>Description</Form.Label>
          <Form.Control type="text" placeholder="Description" value={desc} onChange={handleChange} />
        </Form.Group>
      </Row>

      <div className="d-grid gap-2">
        <Button variant="primary" size="lg">
          Submit
        </Button>
      </div>
    </Form>
  );
}

export default MyForm;
