import { useState } from "react";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";

function MyForm() {
  const [todo, setTodo] = useState({ id: "", title: "", desc: "" });
  const {id, title, desc} = todo;  
  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <Form>
      <Row className="mb-3">
        <Form.Group as={Col} controlId="formGridEmail">
          <Form.Label style={{ color: "white" }}>Title</Form.Label>
          <Form.Control type="text" placeholder="Enter Ttile" value={todo.title}/>
        </Form.Group>

        <Form.Group as={Col} controlId="formGridPassword">
          <Form.Label style={{ color: "white" }}>Description</Form.Label>
          <Form.Control type="text" placeholder="Description" value={todo.desc} />
        </Form.Group>
      </Row>

      <div className="d-grid gap-2">
        <Button variant="primary" size="lg">
          Submit
        </Button>
      </div>
    </Form>
  );
}

export default MyForm;
